import React from "react";
import Card from "../cards/Card";
import TrendModal from "../ModalMovie/modelmovie";
import { useState } from "react";

export default function MovieList({ mytrending }) {
  const [show, setShow] = useState(false);
  const [chosenRecipe, setChosenRecipe] = useState();
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  function handelChosenMovie(data) {
    setChosenRecipe(data);
    handleShow();
  }
  return (
    <>
      {
        mytrending.map(trend => {
          return (
            <>
              <Card cardtrend={trend} handelChosenMovie={handelChosenMovie} />

            </>
          );
        })}
      {chosenRecipe && (
        <TrendModal
          trend={chosenRecipe}
          show={show}
          handleClose={handleClose}
        />
      )}
    </>
  );
}
